if ( ! class_exists( 'Realgymcore_Class_Info_Widget' ) ) {
	/**
	 * Classes list Widget Class
	 *
	 * @return void
	 */
	class Realgymcore_Class_Info_Widget extends WP_Widget {

		/**
		 *  Constructor
		 */
		public function __construct() {
			parent::__construct(
				'realgymcore-class-info-widget',
				esc_html__( 'Realgym Class info', 'realgymcore' ),
				array( 'description' => esc_html__( 'Realgym Class info as a Widget. The content will be shown only on class single page frontpage.', 'realgymcore' ) )
			);
		}

		/**
		 * Widget Frontend Render
		 *
		 * @param array $args     Arguments.
		 * @param array $instance Widget instance.
		 *
		 * @return void
		 * @see WP_Widget
		 */
		public function widget( $args, $instance ) {
			global $post;
			add_action( 'wp_footer', 'realgymcore_get_modal_cf7' );
			$title             = ( ! empty( $instance['title'] ) ) ? $instance['title'] : '';
			$instance['title'] = '';
			if ( ! empty( 'realgym-class' === $post->post_type ) ) { ?>
				<div class="realgym-sidebar-item realgym-class-info">
					<div class="realgym-sidebar-card realgym-card">
						<?php echo wp_kses_post( $args['before_widget'] ); ?>
						<?php if ( $title ) { ?>
							<div class="realgym-card-head">
								<h3 class="realgym-heading-3"><?php echo esc_html( $title ); ?></h3>
							</div>
						<?php } ?>

						<div class="realgym-card-body">
							<ul class="realgym-iconed-list">
								<?php
								$fill_color        = '#0E1722';
								$month_type        = ( get_post_meta( $post->ID, 'class-price-type' ) ) ? get_post_meta( $post->ID, 'class-price-type', true ) : '';
								$classes_info_data = array(
									'class-period'        => array(
										'label' => esc_html__( 'Time', 'realgymcore' ),
										'value' => ( get_post_meta( $post->ID, 'class-period' ) ) ? get_post_meta( $post->ID, 'class-period', true ) : '',
									),
									'class-training-type' => array(
										'label' => esc_html__( 'Training', 'realgymcore' ),
										'value' => ( get_post_meta( $post->ID, 'class-period' ) ) ? get_post_meta( $post->ID, 'class-training-type', true ) : '',
									),
									'class-timetable'     => array(
										'label' => esc_html__( 'Timetable', 'realgymcore' ),
										'value' => $this->get_timetable_days( $post->ID ),
									),
									'class-price'         => array(
										'label' => esc_html__( 'Price', 'realgymcore' ),
										'value' => ( get_post_meta( $post->ID, 'class-price', true ) ) ? get_post_meta( $post->ID, 'class-price', true ) . '/' . $month_type : '',
									),
								);
								foreach ( $classes_info_data as $key => $class_info_data ) :
									if ( $class_info_data['value'] ) :
										?>
										<li class="realgym-iconed-item d-flex align-items-center">
												<span class="realgym-iconed-icon">
													<?php $this->get_svg( $key, $fill_color ); ?>
												</span>
											<span class="text-2"><?php echo esc_html( $class_info_data['label'] ); ?>: <?php echo esc_html( $class_info_data['value'] ); ?></span>
										</li>
										<?php
									endif;
								endforeach;
								?>
							</ul>
							<a href="#" data-bs-toggle="modal" data-bs-target="#realgymcore-join-class" class="realgym-join-button realgym-text-2 text-decoration-none">
								<span> <?php echo esc_html__( 'Join class now', 'realgymcore' ); ?></span>
								<span class="arrow">
										<svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
											<g opacity="1">
													<path d="M8.3485 7.00296L3.11113 1.76537C2.96692 1.6215 2.88759 1.42914 2.88759 1.22403C2.88759 1.01881 2.96692 0.826569 3.11113 0.682472L3.57006 0.223772C3.71404 0.0794472 3.90651 0 4.11162 0C4.31672 0 4.50897 0.0794472 4.65307 0.223772L10.889 6.45958C11.0337 6.60413 11.1129 6.79728 11.1123 7.00262C11.1129 7.20886 11.0338 7.40179 10.889 7.54646L4.65887 13.7762C4.51477 13.9206 4.32253 14 4.11731 14C3.9122 14 3.71996 13.9206 3.57575 13.7762L3.11694 13.3175C2.81838 13.019 2.81838 12.533 3.11694 12.2345L8.3485 7.00296Z"
													fill="white"/>
											</g>
										</svg>
								</span>
							</a>
						</div>

						<?php echo wp_kses_post( $args['after_widget'] ); ?>
					</div>
				</div>
				<?php
			}
		}

		/**
		 * Widget form fields
		 *
		 * @param object $instance Instance of a widget.
		 *
		 * @return void
		 */
		public function form( $instance ) {
			if ( isset( $instance['title'] ) ) {
				$title_escaped = esc_attr( $instance['title'] );
			} else {
				$title_escaped = esc_attr__( 'Class Information', 'realgymcore' );
			}
			?>
			<p>
				<label for="<?php echo esc_attr( $this->get_field_id( 'title' ) ); ?>"><?php esc_html_e( 'Title:', 'realgymcore' ); ?></label>
				<input class="widefat" id="<?php echo esc_attr( $this->get_field_id( 'title' ) ); ?>" name="<?php echo esc_attr( $this->get_field_name( 'title' ) ); ?>" type="text" value="<?php echo esc_attr( $title_escaped ); ?>"/>
			</p>
			<?php
		}

		/**
		 * Widget Form Update
		 *
		 * @param object $new_instance Instance of a widget.
		 * @param object $old_instance Instance of a widget.
		 *
		 * @return array
		 */
		public function update( $new_instance, $old_instance ) {
			$instance          = array();
			$instance['title'] = ( ! empty( $new_instance['title'] ) ) ? wp_strip_all_tags( $new_instance['title'] ) : '';

			return $instance;
		}

