		/**
		 * Get weekdays.
		 *
		 * @param int $post_id post id.
		 *
		 * @return string
		 * @author Balcomsoft
		 */
		public function get_timetable_days( $post_id ) {
			$realgymcore_week_days = ( get_post_meta( $post_id, 'realgymcore-class-week-day' ) ) ? get_post_meta( $post_id, 'realgymcore-class-week-day', true ) : array();

			$wdays     = array(
				'monday'    => esc_html__( 'Mon', 'realgymcore' ),
				'tuesday'   => esc_html__( 'Tue', 'realgymcore' ),
				'wednesday' => esc_html__( 'Wed', 'realgymcore' ),
				'thursday'  => esc_html__( 'Thu', 'realgymcore' ),
				'friday'    => esc_html__( 'Fri', 'realgymcore' ),
				'saturday'  => esc_html__( 'Sat', 'realgymcore' ),
				'sunday'    => esc_html__( 'Sun', 'realgymcore' ),
			);
			$week_days = array();
			foreach ( $realgymcore_week_days as $realgymcore_week_day ) {
				if ( ! empty( $wdays[ $realgymcore_week_day ] ) ) {
					$week_days[] = $wdays[ $realgymcore_week_day ];
				}
			}

			return ( $week_days ) ? implode( ',', $week_days ) : '';
		}
	}

